(function () {
  "use strict";

  // Seleciona los formularios con la clase 'php-email-form'
  let forms = document.querySelectorAll('.php-email-form');

  // Itera sobre los formularios y agrega una clase 'animation-form' para mantener las animaciones
  forms.forEach(function (form) {
    form.classList.add('animation-form');
  });

})();